mapper.update:
UPDATE board
SET writer = #{writer}, title = #{title}, content = #{content}, modified_at = #{modifiedAt}
WHERE board_id = #{boardId}

mapper.select:
SELECT EXISTS (
SELECT 1
FROM board
WHERE board_id = #{boardId}
AND password = #{enteredPassword}
) AS result

SELECT * FROM board WHERE 1=1
AND category_id = #{category}
AND (title LIKE concat('%', #{searchText}, '%') OR content LIKE concat('%', #{searchText}, '%') OR writer LIKE concat('%', #{searchText}, '%'))
AND created_at BETWEEN #{startDate} AND #{endDate}

SELECT COUNT(*) FROM board WHERE 1=1

    AND category_id = #{category}


    AND (title LIKE concat('%', #{searchText}, '%')
    OR content LIKE concat('%', #{searchText}, '%')
    OR writer LIKE concat('%', #{searchText}, '%'))


    AND created_at
    BETWEEN #{startDate}
    AND #{endDate}

mapper.update:
UPDATE board
SET visit_count = #{currentVisitCount} + 1
WHERE board_id = #{boardId}
