mapper.select:
SELECT * FROM board
WHERE 1=1

    AND created_at >= #{startDate}


    AND created_at <= #{endDate}


    AND category_id = #{category}


    AND (title LIKE concat('%', #{search}, '%') OR content LIKE concat('%', #{search}, '%') OR writer LIKE concat('%', #{search}, '%'))

ORDER BY created_at DESC LIMIT #{pageSize}

SELECT COUNT(*) FROM board
WHERE 1=1

    AND created_at >= #{startDate}


    AND created_at <= #{endDate}


    AND category_id = #{category}


    AND (title LIKE concat('%', #{search}, '%') OR content LIKE concat('%', #{search}, '%') OR writer LIKE concat('%', #{search}, '%'))
